import React, { Component } from 'react';
import { IconName } from "react-icons/fc";
import Title from './Title';
import {FcBusinessman,FcLike,FcHome,FcGraduationCap} from "react-icons/fc";

export default class Service extends Component {
    state={
        service:[
            {
                icon:<FcBusinessman/>,
                title:"Wedding Parties",
                //info:'Special offfers in the houses'

            },
            {
                icon:<FcLike/>,
                title:"Birthday parties",
                //info:'Provides shuttle van for travel'

            },
            {
                icon:<FcGraduationCap/>,
                title:"Graduation paties",
                //info:'Our crew helps for boating & hiking'

            },
            {
                icon:<FcHome/>,
                title:"All House parties",
                info:'Special authentic food and world class beverages'

            }
        ]
    }
    
    render() {
        return (
            <section className="services">
               <Title title="Services"/>
               <div className="services-center">
                {this.state.service.map((item,index)=>
                {
                    return(<article key={index} className="service">
                    <span>{item.icon}</span>
                    <h6>{item.title}</h6>
                    <p>{item.info}</p>
                    </article>
                    );
                })}
            </div>
            </section>
        )
    }
}
